import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import Header from '../HeaderComponent/Header';
import './SignUpUser.css';

const googlePlus = require('../../Assets/Assets/google_plus.png');

class SignUpUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          email: '',
          memorymetric: '',
          performancemetric: '',
          clientmetric: '',
          organisation: '',
          databasename: '',
          databasepassword: '',
          databaseip: '',
          emailErr: '',
          databaseipErr: '',
          redirect: false
        }
    }
      validate = () => {
          let validIpformat = /^(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
          let emailvar = this.state.email;
          let databaseipvar = this.state.databaseip;
          let res = true;
          if(emailvar.indexOf('@') === -1) {
              this.setState({emailErr : 'Requires valid email'});
              this.setState({email : ''});
              res = false;
          }
          if(!databaseipvar.match(validIpformat)) {
              this.setState({databaseipErr : 'Requires valid IP'});
              this.setState({databaseip: ''});
              res = false;
          }

          return res;
      }

      handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      }

      // handleChangeUserName = (event) => {
      //   this.setState({ username: event.target.value });
      // }

      // handleChangeEmail = (event) => {
      //   this.setState({ email: event.target.value });
      // }

      // handleChangeMemoryMetric = (event) => {
      //   this.setState({ memorymetric: event.target.value });
      // }

      // handleChangePerformanceMetric = (event) => {
      //   this.setState({ performancemetric: event.target.value });
      // }

      // handleChangeClientMetric = (event) => {
      //   this.setState({ clientmetric: event.target.value });
      // }

      // handleChangeOrganisation = (event) => {
      //   this.setState({ organisation: event.target.value });
      // }

      // handleChangeDatabaseName = (event) => {
      //   this.setState({ databasename: event.target.value });
      // }

      // handleChangeDatabasePassword = (event) => {
      //   this.setState({ databasepassword: event.target.value });
      // }

      // handleChangeDatabaseIP = (event) => {
      //   this.setState({ databaseip: event.target.value });
      // }

      handleSubmit = (event) => {
        event.preventDefault();
        const res = this.validate();
        if(res) {
            this.setState({redirect : true});
        }
      }

    render() {
        if(this.state.redirect === true) {
            return <Redirect to="/Dashboard" />
        }
        return (
            <div className="signup-container">
              <Header heading="Database Configurations" />
              <h2>Database Configurations</h2>
                <div className="form">
                <p className="alignPersonalText">Personal Informations</p>
                <form onSubmit={(e) => this.handleSubmit(e)}>
                <section>
                  <p className="alignInputText">Name:</p>
                  <input type="text" className="inputfield" placeholder="User..." name="username" value={this.state.username} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Email:</p>
                  <input type="text" className="inputfield" placeholder="Email..." name="email" value={this.state.email} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Organisation:</p>
                  <input type="text" className="inputfield" placeholder="Organisation..." name="organisation" value={this.state.organisation} onChange={(e) => this.handleChange(e)} required />
                </section>
                <p className="alignDatabaseText">Database Informations</p>
                <section>
                  <p className="alignInputText">Database Name:</p>
                  <input type="text" className="inputfield" placeholder="Database..." name="databasename" value={this.state.databasename} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Database Password:</p>
                  <input type="password" className="inputfield" placeholder="Password..." name="databasepassword" value={this.state.databasehost} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Database IP:</p>
                  <input type="text" className="inputfield" placeholder="IP..." name="databaseip" value={this.state.databaseip} onChange={(e) => this.handleChange(e)} required/>
                </section>
                <section>
                  <p className="alignInputText">Memory Threshold:</p>
                  <input type="text" className="inputfield" placeholder="Set Memory Limit(Bytes)" name="memorymetric" value={this.state.memorymetric} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Number of Clients</p>
                  <input type="text" className="inputfield" placeholder="Set Limit of Clients" name="clientmetric" value={this.state.clientmetric} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Performance Requirement</p>
                  <input type="text" className="inputfield" placeholder="Enter minimum Performance Required" name="performancemetric" value={this.state.performancemetric} onChange={(e) => this.handleChange(e)} required />
                </section>
                <section>
                  <Link to="/Home"><button className="cancelbutton" type="secondary">Cancel</button></Link>
                  <button className="signupbutton" type="submit">Sign Up</button>
                </section>
            </form>   
            </div>
            </div>
        )
    }
}
export default SignUpUser;
