import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import Header from '../HeaderComponent/Header';
import './SignUpUser.css';

const googlePlus = require('../../Assets/Assets/google_plus.png');

class SignUpUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          email: '',
          memorymetric: '',
          organisation: '',
          databasename: '',
          databasehost: '',
          databaseip: '',
          redirect: false,
          errors: []
        }
    }
      handleChangeUserName = (event) => {
        this.setState({ username: event.target.value });
      }

      handleChangeEmail = (event) => {
        this.setState({ email: event.target.value });
      }

      handleChangeMemoryMetric = (event) => {
        this.setState({ memorymetric: event.target.value });
      }

      handleChangeOrganisation = (event) => {
        this.setState({ organisation: event.target.value });
      }

      handleChangeDatabaseName = (event) => {
        this.setState({ databasename: event.target.value });
      }

      handleChangeDatabaseHost = (event) => {
        this.setState({ databasehost: event.target.value });
      }

      handleChangeDatabaseIP = (event) => {
        this.setState({ databaseip: event.target.value });
      }

      handleSubmit = (event) => {
        if(this.state.username === "") {
          this.showValidationError("username", "Username cannot be empty");
        }
        if(this.state.email === "") {
          this.showValidationError("email", "Email cannot be empty");
        }
        if(this.state.memorymetric === "") {
            this.showValidationError("memorymetric", "Role cannot be empty");
        }
        if(this.state.organisation === "") {
            this.showValidationError("organisation", "Organisation cannot be empty");
        }
        if(this.state.databasename === "") {
            this.showValidationError("databasename", "Database Name cannot be empty");
        }
        if(this.state.databasehost === "") {
            this.showValidationError("databasehost", "Database Host cannot be empty");
        }
        if(this.state.databaseip === "") {
            this.showValidationError("databaseip", "Database IP cannot be empty");
        }

        if(this.state.flag1 && this.state.flag2 && this.state.flag3 && this.state.flag4
            && this.state.flag5 && this.state.flag6 && this.state.flag7 && this.state.flag8) {
          this.setState({ redirect: true});
        }
        event.preventDefault();
      }

    render() {

        return (
            <div className="signup-container">
              <Header />
              <h2>Sign Up</h2>
                <div className="form">
                <button type="submit" className="signupbuttongoogle">
                  <img src={googlePlus} alt="googleplus" className="img"/>
                  <p>Sign in with Google</p>
                </button>
                <hr />
                <p className="alignPersonalText">Personal Informations</p>
                <Form onSubmit={(e) => this.handleSubmit(e)}>
                <section>
                  <p className="alignInputText">Name:</p>
                  <Input type="text" className="inputfield" placeholder="User..." value={this.state.username} onChange={(e) => this.handleChangeUserName(e)} validations={[require]} />
                </section>
                <section>
                  <p className="alignInputText">Email:</p>
                  <Input type="text" className="inputfield" placeholder="Email..." value={this.state.email} onChange={(e) => this.handleChangeEmail(e)} />
                </section>
                <section>
                  <p className="alignInputText">Organisation:</p>
                  <Input type="text" className="inputfield" placeholder="Organisation..." value={this.state.organisation} onChange={(e) => this.handleChangeOrganisation(e)} />
                </section>
                <p className="alignDatabaseText">Database Informations</p>
                <section>
                  <p className="alignInputText">Database Name:</p>
                  <Input type="text" className="inputfield" placeholder="Database..." value={this.state.databasename} onChange={(e) => this.handleChangeDatabaseName(e)} />
                </section>
                <section>
                  <p className="alignInputText">Database Host:</p>
                  <Input type="text" className="inputfield" placeholder="Host..." value={this.state.databasehost} onChange={(e) => this.handleChangeDatabaseHost(e)} />
                </section>
                <section>
                  <p className="alignInputText">Database IP:</p>
                  <Input type="text" className="inputfield" placeholder="IP..." value={this.state.databaseip} onChange={(e) => this.handleChangeDatabaseIP(e)} />
                </section>
                <section>
                  <p className="alignInputText">Memory Threshold:</p>
                  <Input type="text" className="inputfield" placeholder="Set Memory Limit(Bytes)" value={this.state.memorymetric} onChange={(e) => this.handleChangeMemoryMetric(e)} />
                </section>
                <section>
                  <Link to="/Home"><button className="cancelbutton" type="secondary">Cancel</button></Link>
                  <button className="signupbutton" type="submit">Sign Up</button>
                </section>
            </Form>   
            </div>
            </div>
        )
    }
}
export default SignUpUser;
