import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import Header from '../HeaderComponent/Header';
import './SignUpUser.css';

class SignUpUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          email: '',
          password: '',
          role: '',
          organisation: '',
          databasename: '',
          databasehost: '',
          databaseip: '',
          flag1: 0,
          flag2: 0,
          flag3: 0,
          flag4: 0,
          flag5: 0,
          flag6: 0,
          flag7: 0,
          flag8: 0,
          redirect: false,
          errors: []
        }
    }
      showValidationError(elm, mssg) {
        this.setState((prevState) => ( { errors: [...prevState.errors, { elm, mssg} ] }))
      }

      clearValidationError(elm) {
        this.setState((prevState) => {
          let newArr = [];
          for(let err of prevState.errors) {
            if(elm !== err.elm) {
              newArr.push(err);
            }
          }
          return {errors: newArr};
        })
      }

      handleChangeUserName = (event) => {
        this.setState({ username: event.target.value });
        this.clearValidationError("username");
        this.setState({ flag1: 1});
      }

      handleChangeEmail = (event) => {
        this.setState({ email: event.target.value });
        this.clearValidationError("email");
        this.setState({ flag2: 1});
      }

      handleChangePassword = (event) => {
        this.setState({ password: event.target.value });
        this.clearValidationError("password");
        this.setState({ flag3: 1});
      }

      handleChangeRole = (event) => {
        this.setState({ role: event.target.value });
        this.clearValidationError("role");
        this.setState({ flag4: 1});
      }

      handleChangeOrganisation = (event) => {
        this.setState({ organisation: event.target.value });
        this.clearValidationError("organisation");
        this.setState({ flag5: 1});
      }

      handleChangeDatabaseName = (event) => {
        this.setState({ databasename: event.target.value });
        this.clearValidationError("databasename");
        this.setState({ flag6: 1});
      }

      handleChangeDatabaseHost = (event) => {
        this.setState({ databasehost: event.target.value });
        this.clearValidationError("databasehost");
        this.setState({ flag7: 1});
      }

      handleChangeDatabaseIP = (event) => {
        this.setState({ databaseip: event.target.value });
        this.clearValidationError("databaseip");
        this.setState({ flag8: 1});
      }

      handleSubmit = (event) => {
        if(this.state.username === "") {
          this.showValidationError("username", "Username cannot be empty");
        }
        if(this.state.email === "") {
          this.showValidationError("email", "Email cannot be empty");
        }
        if(this.state.password === "") {
          this.showValidationError("password", "Password cannot be empty");
        }
        if(this.state.role === "") {
            this.showValidationError("role", "Role cannot be empty");
        }
        if(this.state.organisation === "") {
            this.showValidationError("organisation", "Organisation cannot be empty");
        }
        if(this.state.databasename === "") {
            this.showValidationError("databasename", "Database Name cannot be empty");
        }
        if(this.state.databasehost === "") {
            this.showValidationError("databasehost", "Database Host cannot be empty");
        }
        if(this.state.databaseip === "") {
            this.showValidationError("databaseip", "Database IP cannot be empty");
        }

        if(this.state.flag1 && this.state.flag2 && this.state.flag3 && this.state.flag4
            && this.state.flag5 && this.state.flag6 && this.state.flag7 && this.state.flag8) {
          this.setState({ redirect: true});
        }
        event.preventDefault();
      }

    render() {
        let usernameErr = null, emailErr = null, passwordErr = null, roleErr = null,
            organisationErr = null, databasenameErr = null, databasehostErr = null, databaseipErr = null;

        for(let err of this.state.errors){
          if(err.elm === "username") {
            usernameErr = err.mssg;
          }
          if(err.elm === "email") {
            emailErr = err.mssg;
          }
          if(err.elm === "password") {
            passwordErr = err.mssg;
          }
          if(err.elm === "role") {
            roleErr = err.mssg;
          }
          if(err.elm === "organisation") {
            organisationErr = err.mssg;
          }
          if(err.elm === "databasename") {
            databasenameErr = err.mssg;
          }
          if(err.elm === "databasehost") {
            databasehostErr = err.mssg;
          }
          if(err.elm === "databaseip") {
            databaseipErr = err.mssg;
          }
        }

        const redirectToReferrer = this.state.redirect;
        if (redirectToReferrer === true) {
            return <Redirect to="/Memory" />
        }

        return (
            <div className="signup-container">
              <Header />
              <div className="form">
                <form onSubmit={(e) => this.handleSubmit(e)}>
                <section>
                  Name:
                  <input type="text" placeholder="User..." value={this.state.username} onChange={(e) => this.handleChangeUserName(e)} />
                  <div>{usernameErr ? usernameErr : ""}</div>
                </section>
                <section>
                  Email:
                  <input type="text" placeholder="Email..." value={this.state.email} onChange={(e) => this.handleChangeEmail(e)} />
                  <div>{emailErr ? emailErr : ""}</div>
                </section>
                <section>
                  Password:
                  <input type="password" placeholder="Password..." value={this.state.password} onChange={(e) => this.handleChangePassword(e)} />
                  <div>{passwordErr ? passwordErr : ""}</div>
                </section>
                <section>
                  Role:
                  <input type="text" placeholder="Role..." value={this.state.role} onChange={(e) => this.handleChangeRole(e)} />
                  <div>{roleErr ? roleErr : ""}</div>
                </section>
                <section>
                  Organisation:
                  <input type="text" placeholder="Organisation..." value={this.state.organisation} onChange={(e) => this.handleChangeOrganisation(e)} />
                  <div>{organisationErr ? organisationErr : ""}</div>
                </section>
                <section>
                  Database Name:
                  <input type="text" placeholder="Database..." value={this.state.databasename} onChange={(e) => this.handleChangeDatabaseName(e)} />
                  <div>{databasenameErr ? databasenameErr : ""}</div>
                </section>
                <section>
                  Database Host:
                  <input type="text" placeholder="Host..." value={this.state.databasehost} onChange={(e) => this.handleChangeDatabaseHost(e)} />
                  <div>{databasehostErr ? databasehostErr : ""}</div>
                </section>
                <section>
                  Database IP:
                  <input type="text" placeholder="IP..." value={this.state.databaseip} onChange={(e) => this.handleChangeDatabaseIP(e)} />
                  <div>{databaseipErr ? databaseipErr : ""}</div>
                </section>
                <section>
                  <button type="submit">Sign Up</button>
                </section>
            </form>   
            </div>
            </div>
        )
    }
}
export default SignUpUser;
