import React, {Component} from 'react';
import Modal from 'react-awesome-modal';
import { Link } from 'react-router-dom';
import './Modal.css';

class ModalMssg extends Component {
    render() {
        const { visible, closeModal, sentData } = this.props;
        let heading = "";
        let message = "";
        let link = "" 
        if(!sentData) {
            heading="Please enter your Database Configurations";
            message="As a first time user, please enter your database configurations for uninterrupted use of tool";
            link="/Signup";
        }
        else {
            heading="Database Configurations Added";
            link="/Dashboard"
        }
        return (
            <section>
                <Modal 
                    visible={visible}
                    width="600"
                    height="300"
                    effect="fadeInUp">
                    <div>
                      <h1>{heading}</h1>
                      <p className="modalmssg">{message}</p>
                      <Link to={link}><button className="continuebutton" onClick={closeModal}>Continue</button></Link>
                    </div>
                </Modal>
            </section>
        );
    }
}

export default ModalMssg;
