import React, {Component} from 'react';
import Modal from 'react-awesome-modal';
import { Link } from 'react-router-dom';
import './Modal.css';

class ModalMssg extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : true
        }
    }

    openModal() {
        this.setState({ visible : true });
    }

    closeModal() {
        this.setState({ visible : false });
    }
    componentWillReceiveProps(prevState, prevProps) {
        if(prevState.visible === false) {
            this.setState({visible : this.props.visibility});
        }
    }
    render() {
        return (
            <section>
                <Modal 
                    visible={this.state.visible}
                    width="600"
                    height="300"
                    effect="fadeInUp">
                    <div>
                      <h1>Please enter your Database Configurations</h1>
                      <p className="modalmssg">As a first time user, please enter your database configurations for uninterrupted use of tool</p>
                      <Link to="/Signup"><button type = "submit" className="continuebutton" onClick={() => this.closeModal()}>Continue</button></Link>
                    </div>
                </Modal>
            </section>
        );
    }
}

export default ModalMssg;
