import React, {Component} from 'react';
import Client from './Client';
import NavBar from './Nav';
import './Chart.css';
import openSocket from 'socket.io-client';
const socket = openSocket('http://localhost:3000');

class ClientChart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      barChartData: {
        labels: [],
        datasets: [
          {
            type: "bar",
            label: "Number of Clients of System",
            backgroundColor: ['orange','orange','orange','orange','orange','orange','orange'],
            borderWidth: "2",
            lineTension: 0.45,
            data: [0, 0, 0, 0, 0, 0, 0]
          }
        ]
      },
      barChartOptions: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          yAxes: [{
              ticks: {
                  beginAtZero:true,
                  min: 0,
                  max: 8016    
              }
            }]
         }
      },
      height: 300,
      memory: null
    };
  }
    change(memory) {
        this.setState(prevState => ({
          memory:(memory/(1024*1024))*10000
        }));
      }

      componentDidMount() {
        socket.on('info', data => {
            this.change(data);
            this.setState({percentage : this.state.memory})
            const oldDataSet = this.state.barChartData.datasets[0];
            const newDataSet = { ...oldDataSet };
            newDataSet.data.push(this.state.memory);
            if(newDataSet.data.length % 8 === 0) {
                newDataSet.data.shift();
              }
            const newChartData = {
                ...this.state.pieChartData,
                datasets: [newDataSet],
                labels: this.state.barChartData.labels.concat( 
                   new Date().toLocaleTimeString()
                 )
               };
               if(newChartData.labels.length % 8 === 0) {
                 newChartData.labels.shift();
               }
              this.setState({ barChartData: newChartData });

        });
      }

      render() {
        return (
          <div>
            <NavBar />
            <div className="chart_size">
              Number of Clients: {this.state.memory}
              <Client data={this.state.barChartData} options={this.state.barChartOptions} height={this.state.height} />
            </div>
          </div>
        );
      }
}

export default ClientChart;
