import React, {Component} from 'react';
import Piechart from './Pie';
import NavBar from './Nav';
import './changeValue.css';
import openSocket from 'socket.io-client';
const socket = openSocket('http://localhost:3000');

class PieChart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pieChartData: {
        labels: [],
        datasets: [
          {
            type: "pie",
            label: "Performance of System",
            backgroundColor: ['orange', 'blue'],
            borderWidth: "2",
            lineTension: 0.45,
            percentage: 0,
            data: []
          }
        ]
      },
      pieChartOptions: {
        responsive: true,
        maintainAspectRatio: false,
      },
      height: 300,
      memory: null
    };
  }
    change(memory) {
        this.setState(prevState => ({
          memory:memory/(1024*1024)
        }));
      }

      componentDidMount() {
        socket.on('info', data => {
            this.change(data);
            this.setState({percentage : this.state.memory})
            const oldDataSet = this.state.pieChartData.datasets[0];
            if(this.state.memory !== oldDataSet.data[0]) {
              const newDataSet = { ...oldDataSet };
              newDataSet.data.splice(0,newDataSet.data.length);
              newDataSet.data.push(this.state.percentage);
              newDataSet.data.push(100 - this.state.percentage);
              const newChartData = {
                ...this.state.pieChartData,
                datasets: [newDataSet],
                labels: this.state.pieChartData.labels.concat( 
                  new Date().toLocaleTimeString()
                 )
               };
               if(newChartData.labels.length % 2 === 0) {
                 newChartData.labels.shift();
               }
              this.setState({ pieChartData: newChartData });
            }
        });
      }

      render() {
        return (
          <div>
            <NavBar />
            <div className="chart_size">
              memory: {this.state.memory}
              <Piechart data={this.state.pieChartData} options={this.state.pieChartOptions} height={this.state.height} />
            </div>
          </div>
        );
      }
}

export default PieChart;
