import React, {Component} from 'react';
import DescriptionComponent from './Components/DescriptionModal/DescriptionModalComponent';
import './HomePage.css';

const homePic = require('../Assets/Assets/bg.jpg');
const rdbAlertLogo = require('../Assets/Assets/logo.png');

class HomePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            redirect: false
        }
    }
    closeModal = () => {
        this.setState({redirect: false});
    }
    openModal = () => {
        this.setState({redirect: true});
    }

    render() {
        return (
            <div className="main-container">
                <img src={homePic} alt="redis" />
                <div className="redisInfo">
                   <img src={rdbAlertLogo} alt="rdbLogo" />
                   <h2>RDBAlert</h2>
                   <button onClick={this.openModal}>Description</button>
                   <DescriptionComponent heading={"The description of redis system:"} visible={this.state.redirect} closeModal={this.closeModal} />
                </div>
            </div>
        )
    }
}

export default HomePage;
