import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../HeaderComponent/Header';
import './SignUpUser.css';

const googlePlus = require('../../Assets/Assets/google_plus.png');

class SignUpUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          email: '',
          memorymetric: '',
          organisation: '',
          databasename: '',
          databasehost: '',
          databaseip: ''
        }
    }
      handleChangeUserName = (event) => {
        this.setState({ username: event.target.value });
      }

      handleChangeEmail = (event) => {
        this.setState({ email: event.target.value });
      }

      handleChangeMemoryMetric = (event) => {
        this.setState({ memorymetric: event.target.value });
      }

      handleChangeOrganisation = (event) => {
        this.setState({ organisation: event.target.value });
      }

      handleChangeDatabaseName = (event) => {
        this.setState({ databasename: event.target.value });
      }

      handleChangeDatabaseHost = (event) => {
        this.setState({ databasehost: event.target.value });
      }

      handleChangeDatabaseIP = (event) => {
        this.setState({ databaseip: event.target.value });
      }

      handleSubmit = (event) => {
        event.preventDefault();
      }

    render() {

        return (
            <div className="signup-container">
              <Header />
              <h2>Sign Up</h2>
                <div className="form">
                <button type="submit" className="signupbuttongoogle">
                  <img src={googlePlus} alt="googleplus" className="img"/>
                  <p>Sign in with Google</p>
                </button>
                <hr />
                <p className="alignPersonalText">Personal Informations</p>
                <form onSubmit={(e) => this.handleSubmit(e)} preventSubmit={true}>
                <section>
                  <p className="alignInputText">Name:</p>
                  <input type="text" className="inputfield" placeholder="User..." value={this.state.username} onChange={(e) => this.handleChangeUserName(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Email:</p>
                  <input type="text" className="inputfield" placeholder="Email..." value={this.state.email} onChange={(e) => this.handleChangeEmail(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Organisation:</p>
                  <input type="text" className="inputfield" placeholder="Organisation..." value={this.state.organisation} onChange={(e) => this.handleChangeOrganisation(e)} required />
                </section>
                <p className="alignDatabaseText">Database Informations</p>
                <section>
                  <p className="alignInputText">Database Name:</p>
                  <input type="text" className="inputfield" placeholder="Database..." value={this.state.databasename} onChange={(e) => this.handleChangeDatabaseName(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Database Host:</p>
                  <input type="text" className="inputfield" placeholder="Host..." value={this.state.databasehost} onChange={(e) => this.handleChangeDatabaseHost(e)} required />
                </section>
                <section>
                  <p className="alignInputText">Database IP:</p>
                  <input type="text" className="inputfield" placeholder="IP..." value={this.state.databaseip} onChange={(e) => this.handleChangeDatabaseIP(e)} required/>
                </section>
                <section>
                  <p className="alignInputText">Memory Threshold:</p>
                  <input type="text" className="inputfield" placeholder="Set Memory Limit(Bytes)" value={this.state.memorymetric} onChange={(e) => this.handleChangeMemoryMetric(e)} required />
                </section>
                <section>
                  <Link to="/Home"><button className="cancelbutton" type="secondary">Cancel</button></Link>
                  <button className="signupbutton" type="submit">Sign Up</button>
                </section>
            </form>   
            </div>
            </div>
        )
    }
}
export default SignUpUser;
