import React, {Component} from 'react';

class ChangeValue extends Component {
    constructor(props) {
        super(props);
        this.state = {
            a : 1,
            b : 1
        }
    }

    change() {
        this.setState(prevState => ({
          a: prevState.a + 1,
          b: prevState.b + 1
        }));
      }

      componentDidMount() {
        this.interval = setInterval(() => this.change(), 1000);
      }

      render() {
        return (
          <div>
            a: {this.state.a};
            b: {this.state.b};
          </div>
        );
      }
}

export default ChangeValue;
