import React, {Component} from 'react';
import Modal from 'react-awesome-modal';
import './DescriptionModalComponent.css';

class DescriptionComponent extends Component {
    constructor(props) {
        super(props);
        this.state ={
            startingdate: '',
            endingdate: ''
        }
    }
    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        this.setState({ redirect : false});
    }
    handleSubmit = (event) => {
        console.log(this.state);
        event.preventDefault();
        this.setState({redirect : true});
    }
    render() {
        const { visible, closeModal, heading  } = this.props;
        return (
            <section>
                <Modal 
                    visible={visible}
                    width="500"
                    height="200"
                    effect="fadeInUp">
                    <div className="description-container">
                      <h1>{heading}</h1>
                      <section>
                      <label>Starting Date:</label>
                      <input type="date" name="startingdate" value={this.state.startingdate} onChange={(e) => this.handleChange(e)} />
                      </section>
                      <section>
                      <label>Ending Date:</label>
                      <input type="date" name="endingdate" value={this.state.endingdate} onChange={(e) => this.handleChange(e)} />
                      </section>
                      <section>
                      <button type="submit" className="proceed-btn" onSubmit={(e) => this.handleSubmit(e)}>Proceed</button>
                      <button className="cancel-btn" onClick={closeModal}>Close</button>
                      </section>
                    </div>
                </Modal>
            </section>
        );
    }
}

export default DescriptionComponent;
