import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import Memory from './Components/Memory';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          email: '',
          password: ''
        }
    }
    handleChangeUserName = (event) => {
        this.setState({ username: event.target.value });
      }

      handleChangeEmail = (event) => {
        this.setState({ email: event.target.value });
      }

      handleChangePassword = (event) => {
        this.setState({ password: event.target.value });
      }

      handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
    //     fetch('http://localhost:8000/api/v1/issues/', {
    //     method: 'POST',
    //     headers: new Headers({
    //       'Access-Control-Allow-Origin': '*',
    //       'Content-Type': 'application/json',
    // }),
    //     body: JSON.stringify(this.state)
    //   }).then(res => res.json())
    //   .then(() => { this.props.history.push('/'); });
      }

      render() {
          return (
            <form onSubmit={(e) => this.handleSubmit(e)}>
              <section>
                Username:
                <input type="text" placeholder="User..." value={this.state.username} onChange={(e) => this.handleChangeUserName(e)} />
              </section>
              <section>
                Email:
                <input type="text" placeholder="Email..." value={this.state.email} onChange={(e) => this.handleChangeEmail(e)} />
              </section>
              <section>
                Password:
                <input type="password" placeholder="Password..." value={this.state.password} onChange={(e) => this.handleChangePassword(e)} />
              </section>
              <section>
                <Link to="/Memory">
                  <button type="submit">Register</button>
                </Link> 
              </section>
            </form>   
          )
      }
}

export default Login;
