import React, {Component} from 'react';
import Performance from './Performance';
import NavBar from './Nav';
import './changeValue.css';
import openSocket from 'socket.io-client';
const socket = openSocket('http://localhost:3000');

class ChangeValue extends Component {
  constructor(props) {
    super(props);
    this.state = {
      lineChartData: {
        labels: [],
        datasets: [
          {
            type: "line",
            label: "Performance of System",
            backgroundColor: "rgba(0, 0, 0, 0)",
            borderWidth: "2",
            lineTension: 0.45,
            data: []
          }
        ]
      },
      lineChartOptions: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
           xAxes: [
             {
               ticks: {
                 autoSkip: true,
                 maxTicksLimit: 10
               },
             }
           ],
           yAxes: [{
               ticks: {
                   beginAtZero:true,
                   min: 0,
                   max: 15.5
               }
             }]
          }
       },
      height: 300,
      memory: null
    };
  }
    change(memory) {
        this.setState(prevState => ({
          memory:memory/(1024*1024)
        }));
      }

      componentDidMount() {
        socket.on('info', data => {
            this.change(data);
            const oldBtcDataSet = this.state.lineChartData.datasets[0];
            const newBtcDataSet = { ...oldBtcDataSet };
            newBtcDataSet.data.push(this.state.memory);
            if(newBtcDataSet.data.length % 8 === 0) {
              newBtcDataSet.data.shift();
            }
            const newChartData = {
              ...this.state.lineChartData,
              datasets: [newBtcDataSet],
              labels: this.state.lineChartData.labels.concat( 
                new Date().toLocaleTimeString()
              )
            };
            if(newChartData.labels.length % 8 === 0) {
              newChartData.labels.shift();
            }
            this.setState({ lineChartData: newChartData });
        });
      }

      render() {
        return (
          <div>
            <NavBar />
            <div className="chart_size">
              memory: {this.state.memory}
              <Performance data={this.state.lineChartData} options={this.state.lineChartOptions} height={this.state.height}/>
            </div>
          </div>
        );
      }
}

export default ChangeValue;
